class Solution {
public:
    static bool compare(const pair<int,int>& a,const pair<int,int>& b){
        return a.second > b.second;
    }
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int,int> diff;

        for(int i=0;i<=n/2;i++){
            int val = abs(nums[i] - nums[n-i-1]);
            diff[val]++;
        }

        int totalcnt = n/2;

        vector<pair<int,int>> mp(diff.begin(),diff.end());
        sort(mp.begin(),mp.end(),compare);

        int minOp = INT_MAX;
        for(auto& [differ,value]: mp){
            int min_req_op = totalcnt - value;
            if(min_req_op >= minOp) break;
            int op = 0;
            for(int i=0;i<n/2;i++){
                if(abs(nums[i] - nums[n-i-1]) == differ) continue;
                if(abs(k - min(nums[i],nums[n-i-1])) >= differ || abs(max(nums[i],nums[n-i-1])) >= differ) op++;
                else op += 2;
            }

            minOp = min(minOp, op);
        }



        return minOp;
    }
};
