class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n= nums.size();
        int i=0;
        int j= n-1;
        map<int,int> mp;
        priority_queue<pair<int,int>> pq;
        while(i<j)
        {
            mp[abs(nums[i]-nums[j])]++;
            i++;
            j--;
        }
        int k1=0;
        int temp=1e9;
        for(auto it: mp)
        {
            pq.push({it.second,it.first});
        }
        while(pq.size()){
            auto it= pq.top();
            pq.pop();
            int d = it.second;
            int i=0;
            int j=n-1;
            int temp2 = 0;
            while(i<j){
                int dif = abs(nums[i] - nums[j]);
                if(dif==d){
                    i++; j--;
                    continue;
                }
                int mini = min(nums[i], nums[j]), maxi = max(nums[i], nums[j]);
                if(maxi>=d || abs(k-mini)>=d) temp2++;
                else temp2+=2;
                i++;
                j--;
            }
            k1++;
            temp = min(temp, temp2);
            if(k1==10) break;
        }
        return temp;
    }
};
