class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int>mp;
        int n = nums.size();
        int answer = 0;
        for (int i=0; i<n/2; i++){

            mp[abs(nums[i] - nums[n-i-1])]++;
        }
        int maxdiff;
        int maxi = -1;
        int prevmax;
        for (auto it:mp){
            if (it.second >= maxi){
                prevmax = maxdiff;
                // maxdiff = it.first;
                if (it.second == maxi){
                    maxdiff = min(it.first,maxdiff);
                }
                else maxdiff = it.first;
                maxi = it.second;
            }
        }
        // cout << prevmax << endl;
        // cout << maxdiff << endl;
        for (int i=0; i<n/2; i++){
            // cout << abs(nums[i] - nums[n-i-1]) << endl;
            if (abs(nums[i] - nums[n-i-1]) != maxdiff){
                int maxdiffpossible=-1;
                maxdiffpossible = max(maxdiffpossible,abs(0-nums[i]));
                maxdiffpossible = max(maxdiffpossible,abs(0-nums[n-i-1]));
                maxdiffpossible = max(maxdiffpossible,abs(k-nums[n-i-1]));
                maxdiffpossible = max(maxdiffpossible,abs(k-nums[i]));
                if (maxdiffpossible < maxdiff) answer+=2;
                else answer++;

                // if (nums[i]+nums[n-i-1] == k) answer+=2;
                // else answer++;
            }
            // cout << answer << endl;
        }
        int answer2 = 0;
        for (int i=0; i<n/2; i++){
            // cout << abs(nums[i] - nums[n-i-1]) << endl;
            if (abs(nums[i] - nums[n-i-1]) != prevmax){
                int maxdiffpossible=-1;
                maxdiffpossible = max(maxdiffpossible,abs(0-nums[i]));
                maxdiffpossible = max(maxdiffpossible,abs(0-nums[n-i-1]));
                maxdiffpossible = max(maxdiffpossible,abs(k-nums[n-i-1]));
                maxdiffpossible = max(maxdiffpossible,abs(k-nums[i]));
                if (maxdiffpossible < prevmax) answer2+=2;
                else answer2++;

                // if (nums[i]+nums[n-i-1] == k) answer+=2;
                // else answer++;
            }
            // cout << answer << endl;
        }
        // cout << answer << " "<< answer2 << endl;
        answer = min(answer,answer2);

        return answer;

    }
};
