class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> m;
        vector<int> v;
        for(int i = 0;i<nums.size()/2;i++){
            int a = nums[i];
            int b = nums[nums.size()-i-1];
            int diff = abs(a-b);
            int maxi = max({a,b,k-a,k-b});
            m[diff].push_back(maxi);
            v.push_back(maxi);
        }
        int mini = nums.size();
        sort(v.begin(),v.end());

        for(auto &val:m){

            int diff = val.first;
            int temp = lower_bound(v.begin(),v.end(),diff)-v.begin();
            temp = temp*2+(nums.size()/2-temp);

            for(auto it:val.second){
                if(it<diff){
                    temp-=2;
                }
                else{
                    temp-=1;
                }
            }

            mini = min(mini,temp);

        }

        return mini;   
    }
};
