class Solution {
public:
int minChanges(vector<int>& nums, int k) {
        vector<int> vct1;
        map<int,vector<int>> mp1;
        for(int i = 0;i<nums.size()/2;i++){
            int a = nums[i],b = nums[nums.size()-i-1];
            int temp1 = abs(a-b);
            int X = max({a,b,k-a,k-b});
            mp1[temp1].push_back(X);
            vct1.push_back(X);
        }
        int ct = nums.size();
        sort(vct1.begin(),vct1.end());

        for(auto &val:mp1){

            int temp1 = val.first;
            int T = lower_bound(vct1.begin(),vct1.end(),temp1)-vct1.begin();
            T = T*2+(nums.size()/2-T);

            for(auto &X:val.second){
                if(X<temp1){
                    T-=2;
                }
                else{
                    T-=1;
                }
            }

            ct = min(ct,T);

        }

        return ct;

    }
};
