class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        diff = Counter()
        # cnt = Counter()
        n = len(nums)
        for i in range(n // 2):
            diff[abs(nums[i] - nums[-1 - i])] += 1
            # cnt[nums[i]]+=1
        diff = sorted([(c, d) for d, c in diff.items()], reverse=True)
        # print(diff)
        ans = n
        last_d = -1
        for c, d in diff:
            if d == last_d:
                continue
            last_d = d
            if d <= k // 2:
                ans = min(ans, n // 2 - c)
                break
            t = 0
            for i, x in enumerate(nums[:n // 2]):
                y = nums[-i - 1]
                if abs(x - y) == d:
                    continue
                if x + d <= k or x >= d or y + d <= k or y >= d:
                    t += 1
                else:
                    t += 2
            if t < ans:
                ans = t
        return ans
