// It took lot of hardwork
class Solution {
    public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,vector<int>> mpp;
        vector<int> vv;

        for(int i=0;i<(n/2);i++)
        {
            int first=nums[i];
            int second=nums[n-i-1];

            int difference=abs(first-second);
            int x=max({first,second,k-first,k-second});

            mpp[difference].push_back(x);
            vv.push_back(x);
        }

        int result=n;
        sort(vv.begin(),vv.end());

        for(auto& [difference,value]:mpp)
        {
            int it=lower_bound(vv.begin(),vv.end(),difference)-vv.begin();
            it=it*2 + (n/2 - it);

            for(auto& ele:value)
            {
                if(ele<difference)
                    it-=2;
                else
                it--;
            }

            result=min(result,it);
        }

        return result;
    }
};
