class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int, int> mp;
        int n = nums.size();
        for (int i = 0; i < n / 2; i++)
        {
            mp[abs(nums[i] - nums[n - 1 - i])]++;
        }
        // int maxi = INT_MIN;
        // for (auto& [sub, freq] : mp)
        //     maxi = max(maxi, freq);

        int ans = INT_MAX;
        for (auto& [sub, freq] : mp)
        {
            // if (freq == maxi)
            // {
                if (n / 2 - freq > ans)
                    continue; 
                int count = 0;
                for (int i = 0; i < n / 2; i++)
                {
                    if (abs(nums[i] - nums[n - 1 - i]) != sub)
                    {
                        if (nums[i] >= sub || nums[i] - k <= -sub || nums[n - i - 1] >= sub || nums[n - i - 1] - k <= -sub)
                            count++;
                        else
                            count += 2;
                    }
                }
                ans = min(count, ans);
            //}
        }
        return ans;
    }
};
