class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        d = {}
        for i in range(len(nums)//2):
            diff = abs(nums[i] - nums[len(nums) - i - 1])
            if(diff not in d):
                d[diff]=0
            d[diff]+=1
        maxi = -1
        vali = -1
        for i,j in d.items():
            if(j>maxi):
                maxi = j
                vali = i
            if(j==maxi):
                if(vali>i):
                    vali = i
        maxi2 = -1
        vali2 = -1
        for i,j in d.items():
            if(vali==i and maxi==j):
                continue
            if(j>maxi2):
                maxi2 = j
                vali2 = i
            if(j==maxi2):
                if(vali2>i):
                    vali2 = i
        ans=0
        for i in range(len(nums)//2):
            diff = abs(nums[i] - nums[len(nums) - i - 1])
            if(diff==vali):
                continue
            else:
                ma = max(nums[i],nums[len(nums) - i - 1])
                mi = min(nums[i],nums[len(nums) - i - 1])
                diff = ma-mi
                if(diff>vali):
                    ans+=1
                else:
                    maa = k
                    diff = maa-mi
                    mii = 0
                    diff2 = ma-mii
                    if(diff>=vali or diff2>=vali):
                        ans+=1
                    else:
                        ans+=2
        ans2=0
        vali = vali2
        maxi = maxi2
        if(maxi==-1):
            return ans
        for i in range(len(nums)//2):
            diff = abs(nums[i] - nums[len(nums) - i - 1])
            if(diff==vali):
                continue
            else:
                ma = max(nums[i],nums[len(nums) - i - 1])
                mi = min(nums[i],nums[len(nums) - i - 1])
                diff = ma-mi
                if(diff>vali):
                    ans2+=1
                else:
                    maa = k
                    diff = maa-mi
                    mii = 0
                    diff2 = ma-mii
                    if(diff>=vali or diff2>=vali):
                        ans2+=1
                    else:
                        ans2+=2
        return min(ans,ans2)
