class Solution {
public:
    int minChanges(vector<int>& nums, int limit) {

        // all the differences
        vector<int> events(limit+2, 0);

        int n  = nums.size();

        for(int i = 0; i < n/2; i++){

            int a = nums[i];
            int b = nums[n-i-1];

            // 0 change
            events[abs(a-b)] -= 1;
            events[abs(a-b) + 1] += 1;

            events[0] += 1;

            int maxi = max(limit - min(a,b), max(a, b));

            events[maxi + 1] += 1;

        }


        int mini = 1e9;
        int ind = -1;

        int sum_changes = 0;

        for(int i = 0; i <= limit; i++){
            sum_changes += events[i];

            if(sum_changes < mini){
                mini = sum_changes;
                ind = i;
            }
        }
        // cout <<ind << endl;
        return mini;
    }
};
