class Solution {
public:
    int getChanges(vector<int>& nums, int diff, int k) {
        int n = nums.size();
        int changes = 0;

        for (int idx=0 ; idx<(n/2) ; idx++) {
            int mn = min(nums[idx], nums[n-1-idx]);
            int mx = max(nums[idx], nums[n-1-idx]);
            int maxDist = max(k - mn, mx);
            int currDiff = mx - mn;

            if (currDiff == diff) continue;

            if (maxDist < diff) changes += 2;
            else changes++;
        }

        return changes;
    }

    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int, int> diffVsCount;
        int maxOccDiff = 0, maxCount = 0;;

        for (int idx=0 ; idx<(n/2) ; idx++) {
            int diff = abs(nums[n-1-idx] - nums[idx]);
            diffVsCount[diff]++;

            if (maxCount < diffVsCount[diff]) {
                maxCount = diffVsCount[diff];
                maxOccDiff = diff;
            }
        }

        int diff = 0, diffCount = 0;
        int maxDist1 = 0;
        for (int idx=0 ; idx<(n/2) ; idx++) {
            int mn = min(nums[idx], nums[n-1-idx]);
            int mx = max(nums[idx], nums[n-1-idx]);
            int maxDist = max(k - mn, mx);

            if (maxDist < maxOccDiff) {
                int currDiff = mx - mn;
                if (diffVsCount[currDiff] > diffCount) {
                    diffCount = diffVsCount[currDiff];
                    diff = currDiff;
                    maxDist1 = maxDist;
                }
            }
        }

        int diff1 = 0, diffCount1 = 0;
        for (auto [dif, difCnt] : diffVsCount) {
            if (dif <= maxDist1) {
                if (diffCount1 < difCnt) {
                    diffCount1 = difCnt;
                    diff1 = dif;
                }
            }
        }

        cout << maxOccDiff << " " << diff << " " << diff1;

        return min({getChanges(nums, maxOccDiff, k), getChanges(nums, diff, k), getChanges(nums, diff1, k)});
    }
};
