class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
      map<int,vector<int>> m;
        vector<int> v;
        for(int i = 0;i<nums.size()/2;i++){
            int a = nums[i];
            int b = nums[nums.size()-i-1];
            int diff = abs(a-b);
            int temp = max({a,b,k-a,k-b});
            m[diff].push_back(temp);
            v.push_back(temp);
        }
        int n = nums.size();
        sort(v.begin(),v.end());

        for(auto i:m){

            int diff = i.first;
            int lower = lower_bound(v.begin(),v.end(),diff)-v.begin();
            lower = lower*2+(nums.size()/2-lower);

            for(auto j:i.second){
                if(j<diff){
                    lower-=2;
                }
                else{
                    lower-=1;
                }
            }

            n = min(n,lower);

        }

        return n;
    }
};
