class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int> m;
        int l=0,r=nums.size()-1;
        int tot=0;
        int S[100005];
        fill(S,S+k+2,0);
        while(l<r){
            int a=nums[l],b=nums[r];
            if(a>b)swap(a,b);
            if(a==b){
                S[1]++;
                S[max(k-a,a)+1]++;
            }
            else{
                S[0]++;
                S[abs(a-b)]--;
                S[abs(a-b)+1]++;
                S[max(k-a,b)+1]++;
            }
            l++;
            r--;
        }
        int sum=0;
        int Max=1e9;
        for(int i = 0;i<=k;i++){
            sum+=S[i];
            Max=min(Max,sum);
        }
       // for(auto it:m)Max=max(Max,it.second);
        return Max;
    }
};
