class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mp;
        vector<int> v;
        int n=nums.size();
        for(int i = 0;i<n/2;i++){
            int a = nums[i];
            int b = nums[nums.size()-i-1];
            int d = abs(a-b);
            int maxi = max({a,b,k-a,k-b});
            mp[d].push_back(maxi);
            v.push_back(maxi);
        }
        int ans = nums.size();
        sort(v.begin(),v.end());

        for(auto &val:mp){

            int d = val.first;
            int t = lower_bound(v.begin(),v.end(),d)-v.begin();
            t = t*2+(nums.size()/2-t);

            for(auto &i:val.second){
                if(i<d){
                    t-=2;
                }
                else{
                    t-=1;
                }
            }

            ans = min(ans,t);

        }

        return ans;
    }
};
