class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> diffMap; 
        vector<int> xVals; 

        for (int i = 0; i < nums.size() / 2; ++i) {
            int a = nums[i];
            int b = nums[nums.size() - i - 1];
            int d = abs(a - b);
            int x = max({a, b, k - a, k - b});
            diffMap[d].push_back(x);
            xVals.push_back(x);
        }

        int minChgs = nums.size(); 
        sort(xVals.begin(), xVals.end()); 

        for (auto& entry : diffMap) {
            int d = entry.first;
            int cnt = lower_bound(xVals.begin(), xVals.end(), d) - xVals.begin();
            cnt = cnt * 2 + (nums.size() / 2 - cnt); 

            for (int x : entry.second) {
                if (x < d) {
                    cnt -= 2; 
                } else {
                    cnt -= 1;  
                }
            }

            minChgs = min(minChgs, cnt); 
        }

        return minChgs;
    }
};
