class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>>mp;
        vector<int>v;
        int n = nums.size();
        for(int i =0;i<n/2;i++){
            int diff = abs(nums[i]-nums[n-i-1]);
            int x = max({nums[i],nums[n-i-1],k-nums[i],k-nums[n-i-1]});
            mp[diff].push_back(x);
            v.push_back(x);
        }
        sort(v.begin(),v.end());
        int ans = n;
        for(auto &it:mp){
            int diff = it.first;
            int T = lower_bound(v.begin(),v.end(),diff)-v.begin();
            T = T*2+(n/2-T);
            for(auto x :it.second){
                if(x<diff){
                    T-=2;
                }else{
                    T-=1;
                }

            }
            ans = min(ans,T);
        }
        return ans;
    }
};
