func minChanges(nums []int, k int) int {
	var arr []int
	Map := make(map[int][]int)
	halfLength := len(nums) / 2
	for i := 0; i < halfLength; i++ {
		a, b := nums[i], nums[len(nums)-i-1]
		a -= b
		if a < 0 {
			a = -a
		}
		if ta, ok := Map[a]; ok {
			Map[a] = append(ta, i)
		} else {
			Map[a] = []int{i}
			arr = append(arr, a)
		}
	}
	sort.Slice(arr, func(i, j int) bool {
		return len(Map[arr[i]]) >= len(Map[arr[j]])
	})
	min := -1
	for i := 0; i < len(arr); i++ {
		gap := arr[i]
		if min != -1 && min <= halfLength-len(Map[arr[i]]) {
			break
		}
		var sum int
		var end bool
		for j := 0; j < len(arr); j++ {
			if i == j {
				continue
			}
			ta := Map[arr[j]]
			for t := 0; t < len(ta); t++ {
				index := ta[t]
				a, b := nums[index], nums[len(nums)-index-1]
				r := leetcode100365testGet(a, b, k, gap)
				if r == -1 {
					end = true
					break
				}
				sum += r
			}
			if end {
				break
			}
		}
		if end {
			continue
		}
		if min == -1 || min > sum {
			min = sum
		}
	}
	return min
}
func leetcode100365testGet(a, b, k, gap int) int {
	if a-gap >= 0 || a+gap <= k || b-gap >= 0 || b+gap <= k {
		return 1
	}
	if gap <= k {
		return 2
	}
	return -1
}
