class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int>m;
        int maxi=INT_MIN;
        int diff=0;
        int n=nums.size();
        for(int i=0;i<n/2;i++){
            m[abs(nums[i]-nums[n-1-i])]++;
            if(m[abs(nums[i]-nums[n-1-i])]>maxi){
               maxi= m[abs(nums[i]-nums[n-1-i])];
                diff=abs(nums[i]-nums[n-1-i]);
            }
            else if(m[abs(nums[i]-nums[n-1-i])]==maxi){

            diff=min(diff,abs(nums[i]-nums[n-1-i]));    
            }
        }
        int fans=INT_MAX;
        for(auto ele:m){
            if(n/2-ele.second>fans)continue;
        diff=ele.first;
            int ans=0;
        for(int i=0;i<n/2;i++){

            int req;
            if(abs(nums[i]-nums[n-i-1])==diff)continue;
            if(nums[i]<diff){
                req=diff+nums[i];
            }
            else {
                req=nums[i]-diff;
            }  
            if(nums[n-i-1]<diff){
                req=min(req,diff+nums[n-1-i]);

            }
            else {
                    req=min(req,nums[n-i-1]-diff);
            }
            if(req>k||req<0)ans+=2;
            else ans+=1;
        }
            fans=min(fans,ans);
        }
        return fans;

    }
};
