class Solution {
public:
    int minChanges(vector<int>& snum, int k) {
        long long maxi=*max_element(snum.begin(),snum.end());
        unordered_map<long long,vector<long long>> smp;
        vector<long long> temp;
        for(long long i = 0;i<snum.size()/2;i++){
            long long a = snum[i],b = snum[snum.size()-i-1];
            long long sd = abs(a-b);
            long long sx = max({a,b,k-a,k-b});
            smp[sd].push_back(sx);
            temp.push_back(sx);
        }
        long long sahilans = snum.size();
        sort(temp.begin(),temp.end());
        for(auto it:smp){
            long long sd = it.first;
            long long tmp = lower_bound(temp.begin(),temp.end(),sd)-temp.begin();
            tmp = tmp*2+(snum.size()/2-tmp);
            for(auto i:it.second){
                if(i<sd)tmp-=2;
                else  tmp-=1;
            }
            sahilans = min({sahilans,tmp});
        }

        return sahilans;
    }
};
