class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> v(k+2);
        for(int i = 0; i < (n/2); i++) {
            v[0]++;
            int mini = min(nums[i],nums[n-i-1]);
            int x = k-mini;
            x = max(x,max(nums[i],nums[n-i-1]));
            v[x+1]++;
        }
        for(int i = 1; i <= k; i++) {
            v[i] += v[i-1];
        }
        // for(int i = 0; i <= k; i++) {
        //     cout << v[i] << " ";
        // }
        // cout << endl;
        for(int i = 0; i < (n/2); i++) {
            v[abs(nums[i]-nums[n-i-1])]--;
        }
        int ans = n;
        for(int i = 0; i <= k; i++) {
            // cout << v[i] << " ";
            ans = min(ans,v[i]);
        }
        // cout << endl;
        return ans;
    }
};
