class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int>m;
        // vector<int>v;
        int n=nums.size();
        for(int i=0;i<nums.size()/2;i++){
            m[abs(nums[i]-nums[n-1-i])]++;
        }
        int pos=-1;
        int cnt=0;
        vector<int>v(1e5+1,0);
        for(auto it:m){
            if(it.second>cnt){
                cnt=it.second;
                pos=it.first;
            }
            v[it.first]=it.second;
        } 
        sort(v.begin(),v.end());
        int freq=v[v.size()-2];
        int pos2;
        for(auto it:m){
            if(it.second==freq){
                pos2=it.first;
            }
        }
        int ans2=0;
        int ans=0;
        for(int i=0;i<nums.size()/2;i++){
            int dif=abs(nums[i]-nums[n-i-1]);
            int a=nums[i];
            int b=nums[n-1-i];
            if(a>b)swap(a,b);
            if(dif!=pos){
                if(pos+nums[i]<=k){
                    ans++;
                }
                else if(pos+nums[n-i-1]<=k){
                    ans++;
                }
                else if(b-pos<=k && b-pos>=0)ans++;
                else if(a+pos<=k)ans++;
                else{
                    ans+=2;
                }
            }
        }
        for(int i=0;i<nums.size()/2;i++){
            int dif=abs(nums[i]-nums[n-i-1]);
            int a=nums[i];
            int b=nums[n-1-i];
            if(a>b)swap(a,b);
            if(dif!=pos2){
                if(pos2+nums[i]<=k){
                    ans2++;
                }
                else if(pos2+nums[n-i-1]<=k){
                    ans2++;
                }
                else if(b-pos2<=k && b-pos2>=0)ans2++;
                else if(a+pos2<=k)ans2++;
                else{
                    ans2+=2;
                }
            }
        }
        // cout<<pos;
        // cout<<pos2;
        return min(ans2,ans);

    }
};
