import java.util.Arrays;
import java.util.HashMap;
import java.util.Map;

class Solution {
    public int minChanges(int[] nums, int k) {
        int n = nums.length;
        int[] abss = new int[k + 5];
        int[] maxs = new int[k + 5];
        for (int i = 0; i < n / 2; ++i) {
            int l = nums[i], r = nums[n - 1 - i];
            int abs = Math.abs(l - r);
            ++abss[abs];
            int x = Math.min(l, r), y = Math.max(l, r);
            ++maxs[Math.max(y, k - x)];
        }
        int[] s = new int[k + 5];
        s[0] = maxs[0];
        for (int i = 1; i <= k; ++i) {
            s[i] = s[i - 1] + maxs[i];
        }
        int ans = Integer.MAX_VALUE;
        for (int i = 0; i <= k; ++i) {
            if (abss[i] == 0) {
                continue;
            }
            int l = i - 1 >= 0 ? s[i - 1] : 0, r = s[k] - l;
            ans = Math.min(ans, l * 2 + r - abss[i]);
        }
        return ans;
    }
}

