class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        // line sweep algo; 

        vector<int> ops(k+2); 
        for(int i = 0;i<nums.size()/2;i++){
            int mini = min(nums[i],nums[nums.size()-i-1]); 
            int maxi = max(nums[i],nums[nums.size()-i-1]); 

            // o -> maxi one ops;
            int diff = max(k-mini,maxi); 
            ops[0] += 1; 
            ops[diff+1] -= 1; 

            ops[diff+1] += 2; 

            ops[maxi-mini] -= 1; 
            //cout << maxi -mini << endl; 
            ops[maxi-mini+1] += 1;
        }


        int mini = ops[0]; 
       //  cout << ops[0] << " "; 
        for(int i = 1;i<ops.size()-1;i++){
              ops[i] += ops[i-1]; 
              mini = min(mini,ops[i]); 
             // cout << ops[i] <<  " "; 
            //  return ; 
        }
       // cout << endl; 

        // for(int i : o[s])

        return mini; 

    }
};
