class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int>preSum(k+2, 0);
        int mini, maxi;
        int n = nums.size();
        int point;
        for(int i=0;i<n/2;i++)
        {
            mini=min(nums[i], nums[n-1-i]);
            maxi = max(nums[i], nums[n-i-1]);
            preSum[0]++;
            preSum[maxi-mini]--;
            preSum[maxi-mini+1]++;
            point= max(maxi, max(k-maxi, k-mini));
            preSum[point+1]++;

        }
        int ans =preSum[0];
        cout<<preSum[0]<<" ";
        for(int i=1;i<=k;i++)
        {
            preSum[i]+=preSum[i-1];
            cout<<preSum[i]<<" ";
            ans=min(ans, preSum[i]);

        }
        cout<<endl;
        return ans;
    }
};
