#include <vector>
#include <map>
#include <algorithm>
using namespace std;

class Solution {
public:
    int minChanges(vector<int>& a, int b) {
        map<int, vector<int>> c;
        vector<int> d;
        for(int e = 0; e < a.size() / 2; e++) {
            int f = a[e], g = a[a.size() - e - 1];
            int h = abs(f - g);
            int i = max({f, g, b - f, b - g});
            c[h].push_back(i);
            d.push_back(i);
        }
        int j = a.size();
        sort(d.begin(), d.end());

        for(auto &k : c) {
            int l = k.first;
            int m = lower_bound(d.begin(), d.end(), l) - d.begin();
            m = m * 2 + (a.size() / 2 - m);

            for(auto &n : k.second) {
                if(n < l) {
                    m -= 2;
                } else {
                    m -= 1;
                }
            }

            j = min(j, m);
        }

        return j;
    }
};
