class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        dictionary = {}
        lst = []
        n = len(nums)
        p = len(nums) -k + 1
        for i in range(n // 2):

            a, b = nums[i], nums[n - i - 1]
            v = abs(a - b)
            X = max(a, b, k - a, k - b)

            if v not in dictionary:
                dictionary[v] = []

            dictionary[v].append(X)
            lst.append(X)

        result = n
        lst.sort()

        for a, b in dictionary.items():

            # q = k+1-a+b

            T = bisect.bisect_left(lst, a)

            T = T * 2 + (n // 2 - T)

            for j in b:

                if j< a:
                    T -= 2

                else:
                    T -= 1

            result = min(result, T)

        return result
