class Solution {
    public int minChanges(int[] nums, int k) {
        int n = nums.length;
        int[] ct = new int[k+1];
        for(int i = 0;i<n/2;i++){
            int min = Math.min(nums[i],nums[n-1-i]);
            int max = Math.max(nums[i],nums[n-1-i]);
            int diff = max-min;
            int r = Math.max(max,k-min);
            ct[0]++;
            ct[diff]--;
            if(diff+1<=r) ct[diff+1]++;
            if(r+1<=k)ct[r+1]++;
        }
        int ret = n;
        int sum = 0;
        for(int i = 0;i<=k;i++){
            sum+=ct[i];
            ret = Math.min(ret,sum);
        }   
        return ret;
    }
}
