class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int ans = 0;
        map<int,int> mp;
        for(int i=0;i<nums.size()/2;i++){
            int diff = abs(nums[i] - nums[n-i-1]);
            mp[diff]++;
        }
        int maxicount = 0;
        int maxi = 0;
        int secondmaxicount = 0;
        int secondmaxi = 0;
        for(auto it :mp){
            int count = it.second;
            if(maxicount < count){
                secondmaxicount = maxicount ;
                secondmaxi = maxi;
                maxicount= count;
                maxi = it.first;
            }
            else if(maxicount > count && secondmaxicount  < count){
                secondmaxicount = count;
                secondmaxi = it.first;
            }
        }
        // cout<<maxi<<endl;
        // int ans = 0;
        for(int i=0;i<n/2;i++){
            int diff = abs(nums[i] - nums[n-i-1]);
            if(diff != maxi){
                int maxidiff = diff;
                maxidiff = max(maxidiff,abs(k-nums[n-i-1]));
                maxidiff = max(maxidiff,abs(nums[i] - k));
                maxidiff = max(maxidiff,nums[i]);
                maxidiff = max(maxidiff,nums[n-i-1]);
                if(maxidiff >= maxi){
                    // cout<<maxidiff<<endl;
                    ans++;
                }else{
                    ans+=2;
                }
            }
        }
        cout<<secondmaxi<<endl;
        int ans2 = 0;
        for(int i=0;i<n/2;i++){
            int diff = abs(nums[i] - nums[n-i-1]);
            if(diff != secondmaxi){
                int maxidiff = diff;
                maxidiff = max(maxidiff,abs(k-nums[n-i-1]));
                maxidiff = max(maxidiff,abs(nums[i] - k));
                maxidiff = max(maxidiff,nums[i]);
                maxidiff = max(maxidiff,nums[n-i-1]);
                if(maxidiff >= secondmaxi){
                    // cout<<maxidiff<<endl;
                    ans2++;
                }else{
                    ans2+=2;
                }
            }
        }
        ans = min(ans,ans2);
        return ans;

    }
};
