class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int ps[k+1];
        for(int i=0;i<=k;i++) ps[i] = 0;
        int n = nums.size();
        int i=0,j=n-1; 
        while(i<j){
            int a = nums[i], b = nums[j];
            ps[0]++;
            int maxx_1 = max(max(b,abs(k-b)), max(a,abs(k-a)));
            if(maxx_1<k){
                ps[maxx_1+1]++;
            }
            i++;j--;
        }

        for(int i=1;i<=k;i++){
            ps[i] = ps[i-1] + ps[i];
        }

        i=0,j=n-1;

        while(i<j){
            ps[abs(nums[i]-nums[j])]--;
            i++;j--;
        }

        int ans = 100000000;

        for(i=0;i<=k;i++){
            ans = min(ans,ps[i]);
        }
        return ans;
    }
};
