class Solution {
    public int minChanges(int[] nums, int k) {
        Map<Integer,Integer> map = new HashMap<>();
        int n = nums.length;
        int ans = n;
        for(int i=0;i<n/2;i++){
            int val = Math.abs(nums[i]-nums[n-i-1]);
            map.put(val,1+map.getOrDefault(val,0));
        }
        List<Map.Entry<Integer,Integer>> list = new ArrayList<>(map.entrySet());
        Collections.sort(list,(a,b)->b.getValue().compareTo(a.getValue()));
        for(int i=0;i<list.size();i++){
            int key = list.get(i).getKey();
            int tmp = 0;
            boolean flag = true;
            for(int j=0;j<n/2;j++){
                int t1 = Math.abs(nums[j]-nums[n-j-1]);
                if(t1!=key){
                    boolean flag1 = key+nums[n-j-1]>=0&&key+nums[n-j-1]<=k;
                    boolean flag2 = nums[n-j-1]-key>=0&&nums[n-j-1]-key<=k;
                    boolean flag3 = nums[j]-key>=0&&nums[j]-key<=k;
                    boolean flag4 = key+nums[j]>=0&&key+nums[j]<=k;
                    if(flag1||flag2||flag3||flag4){
                        tmp++;
                        continue;
                    }
                    if(k>=key){
                        tmp+=2;
                        continue;
                    }
                    flag = false;
                    break;
                }
            }
            if(flag){
                if(ans>tmp){
                    ans = tmp;
                }else{
                    if(i<list.size()-1){
                        if(ans<(n-list.get(i+1).getValue())/2){
                            break;
                        }
                    }
                }
            }
        }
        return ans;
    }
}
