class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mpp;
        vector<int> v;
        for(int i = 0;i<nums.size()/2;i++){
            int a = nums[i],b = nums[nums.size()-i-1];
            int diff = abs(a-b);
            int l= max({a,b,k-a,k-b});
            mpp[diff].push_back(l);
            v.push_back(l);
        }
        int ans = nums.size();
        sort(v.begin(),v.end());

        for(auto &data:mpp){

            int diff = data.first;
            int lower = lower_bound(v.begin(),v.end(),diff)-v.begin();
            lower = lower*2+(nums.size()/2-lower);

            for(auto &X:data.second){
                if(X<diff){
                    lower-=2;
                }
                else{
                    lower-=1;
                }
            }

            ans = min(ans,lower);

        }

        return ans;

    }
};
