class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<int, vector<int>> map;
        vector<int> v;

        for(int i=0; i<n/2; i++) {
            int diff = abs(nums[i] - nums[n-i-1]);
            int x = max({nums[i], nums[n-i-1], k - nums[i], k - nums[n-i-1]});

            map[diff].push_back(x);
            v.push_back(x);
        }

        int ans = INT_MAX;  
        sort(v.begin(), v.end());

        for(auto [diff, values] : map) {
            int temp = lower_bound(v.begin(), v.end(), diff) - v.begin();
            temp = temp * 2 + (n/2 - temp);

            for(auto val : values) {
                if(val < diff) {
                    temp -= 2;
                } 
                else {
                    temp -= 1;
                }
            }

            ans = min(ans, temp);
        }
        return ans;
    }
};
