class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
          vector<int> vec;
        int n=nums.size();
        map<int,vector<int>> mp;

        for(int i=0;i<n/2;i++)
        {
            int a=nums[i];
            int b=nums[n-i-1];
            int diff=abs(b-a);
            int x=max({a,b,k-a,k-b});
             vec.push_back(x);
            mp[diff].push_back(x);

        }
        int res=n;
        reverse(vec.begin(),vec.end());
        sort(vec.begin(),vec.end());
        for(auto it:mp)
        {
            int diff=it.first;
            int C=lower_bound(vec.begin(),vec.end(),diff)-vec.begin();
            C=C*2+(n/2 -C);
            for(auto j:it.second)
            {
                if(j<diff)
                {
                    C-=2;
                 }
                else{
                    C-=1;
                }
            }
            res=min(res,C);
        }
        return res;
    }
};
