class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mp;
        vector<int> newnums;
        int n=nums.size();
        for(int i = 0;i<n/2;i++){
            int x = max({nums[i],nums[n-i-1],k-nums[i],k-nums[n-i-1]});
            mp[abs(nums[i]-nums[n-i-1])].push_back(x);
            newnums.push_back(x);
        }
        int ans = n;
        sort(newnums.begin(),newnums.end());

        for(auto &val:mp){
            int check = lower_bound(newnums.begin(),newnums.end(),val.first)-newnums.begin();
            check = check*2 + (n/2-check);

            for(auto &x:val.second){
                if(x<val.first){
                    check=check-2;
                }
                else{
                    check=check-1;
                }
            }
            ans = min(ans,check);
        }
        return ans;
    }
};
