class Solution {
    int preSum[100005];
    int cnt[100005];
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int m = n / 2;
        for(int i = 0; i < m; i ++) {
            int l = min(nums[i], nums[n - 1 - i]), r = max(nums[i], nums[n - 1 - i]);
            preSum[0] ++, preSum[max(k - l, r) + 1] --; 
            cnt[r - l] ++;
        }
        for(int i = 1; i <= k; i ++) {
            preSum[i] += preSum[i - 1];
        }
        int res = n;
        for(int x = 0; x <= k; x ++) {
            res = min(res, preSum[x] + 2 * (m - preSum[x]) - cnt[x]);
        }
        return res;
    }
};
