class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> mp;
        vector<int> vec;
        for (int i = 0; i < nums.size() / 2; ++i) {
            int a = nums[i];
            int b = nums[nums.size() - i - 1];
            int diff = abs(a - b);
            int X = max({a, b, k - a, k - b});
            mp[diff].push_back(X);
            vec.push_back(X);
        }
        int ans = nums.size();  
        sort(vec.begin(), vec.end());
        for (const auto& [diff, values] : mp) {
            int s = lower_bound(vec.begin(), vec.end(), diff) - vec.begin();
            s = s * 2 + (nums.size() / 2 - s);

            for (const auto& X : values) {
                if (X < diff) {
                    s -= 2;
                } else {
                    s -= 1;
                }
            }

            ans = min(ans, s);
        }

        return ans;
    }
};
