class Solution {
public:
    int h(vector<int>&nums,int fin,int k)
    {
        int n=nums.size();
       int i=0;int j=n-1;int ans=0;
        while(i<n && j>=0 && i<j)
        {
            if(abs(nums[i]-nums[j])==fin)
            {
                i++;j--;
            }
            else
            {
                if(nums[i]+fin<=k || nums[i]-fin>=0 ||nums[j]+fin<=k ||nums[j]-fin>=0)ans+=1;
                else ans+=2;
                i++;j--;
            }
        }
        return ans;
    }
    static bool comp(pair<int,int>&a ,pair<int,int>&b)
    {
        if(a.first>b.first)return true;
            else if(a.first==b.first)
            {
                return a.second<b.second;
            }
            else return false;
    }
    int minChanges(vector<int>& nums, int k) {
        int maxcnt=0,diff=0,fin=0;
        vector<pair<int,int>>vpp;
        vector<int>arr(1e5,0);
        int n=nums.size(),last=0;
        int i=0,j=n-1;
        while(i<n && j>=0 && i<j)
        {
            diff=abs(nums[i]-nums[j]);
            arr[diff]++;
            i++;
            j--;
        }
        for(int i=0;i<1e5;i++)
        {
            if(arr[i]!=0)
            {
                vpp.push_back({arr[i],i});
            }
        }
        sort(vpp.begin(),vpp.end() ,comp);int mini=1e5;
        for(int i=0;i<vpp.size();i++)
        {
            if(vpp[i].first==last)continue;
            mini=min(mini,h(nums,vpp[i].second,k));
            last=vpp[i].first;

        }
        return mini;



    }
};
