class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int res = 1e9;
        int cnt = 0;
        int X;
        unordered_map<int, int>mp;
        vector<int> XX;
        int l = 0, r = n - 1;
        while(l < r) {
            int t = abs(nums[l] - nums[r]);
            mp[t]++;
            if(mp[t] > cnt) {
                X = t;
                cnt = mp[t];
            }
            l++;
            r--;
        }
        for(auto [x, y] : mp){
            if(y == cnt || y == cnt - 1) {
                XX.push_back(x);
            }
        }
        int i = 0;
        while(i < XX.size()) {
            int ans = 0;
            l = 0, r = n - 1;
            X = XX[i];
            //cout<<"XXX-----"<<X<<endl;
            while(l < r) {
            int t = abs(nums[l] - nums[r]);
            if(t != X) {
                //cout<<l<<':'<<r<<endl;
                //cout<<nums[l]<<"----"<<nums[r]<<endl;
                if(k - nums[l] >= X || k - nums[r] >= X) {
                    ans += 1;
                    //cout<<"a  "<<1<<endl;
                }
                else if(nums[l] >= X || nums[r] >= X){
                    ans += 1;
                    //cout<<"b  "<<1<<endl;
                }
                else if(nums[l] <= k - X || nums[r] <= k - X){
                    ans += 1;
                    //cout<<"c  "<<1<<endl;
                }
                else {
                    ans += 2;
                    //cout<<2<<endl;
                    }
                }
                l++;
                r--;
            }
            i++;
            res = min(ans, res);
        }
        return res;
    }
};
