class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int, vector<int>> mpp;
        int n = nums.size();
        int maxOcc = -1, maxCnt=0;
        vector<int> arr;
        for(int i=0;i<n/2;i++){
            int diff = abs(nums[i]-nums[n-i-1]);
            mpp[diff].push_back(max({nums[i], nums[n-i-1], k-nums[i], k-nums[n-i-1]}));
            arr.push_back(mpp[diff].back());
        }

        int ans=1e9;
        sort(arr.begin(),arr.end());
        for(auto& p : mpp){
            int diff = p.first;
            int ind = lower_bound(arr.begin(),arr.end(),diff) - arr.begin();
            ind = (n/2- ind) + ind*2;
            for(auto &val : p.second){
                if(val<diff) ind-=2;
                else ind--;
            }
            ans = min(ans,ind);
        }
        return ans;
    }
};



