class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int len = nums.size();
        unordered_map<int, int> gap_num;
        int max_occur = 0, max_occur_num = 0;
        for(int i = 0; i < len / 2; i++) {
            int g = abs(nums[i] - nums[len - 1 - i]);
            if(gap_num.find(g) != gap_num.end()){
                gap_num[g] += 1;
            } else {
                gap_num[g] = 1;
            }
            if(gap_num[g] > max_occur_num) {
                max_occur = g;
                max_occur_num = gap_num[g];
            }
        }

        vector<pair<int, int>> occur_num;
        for(auto& it: gap_num) {
            // make_pair<int, int>()
            occur_num.push_back(it);
        }
        sort(occur_num.begin(), occur_num.end(), [](pair<int, int> x, pair<int, int> y){
            return x.second > y.second;
        });

        // cout << max_occur << "," << max_occur_num << endl;
        int final = 1e9;
        // for(auto& it: occur_num) {
        for(int j = 0; j < occur_num.size(); j++) {
            auto& it = occur_num[j];
            max_occur = it.first;
            max_occur_num = it.second;
            int res = 0;
            for (int i = 0; i < len / 2; i++) {
                // find all != max
                int g = abs(nums[i] - nums[len - 1 - i]);
                int max_gap = max(k - min(nums[i], nums[len - 1 - i]), max(nums[i], nums[len - 1 - i]));
                // cout << nums[i] << "," << nums[len - 1 - i] << "," << max_gap << endl;
                if(g != max_occur) {
                    // int t = abs(g - max_occur);
                    if (max_occur <= max_gap) {
                        res += 1;
                    } else {
                        res += 2;
                    }
                }
            }
            final = min(final, res);
            if(j < occur_num.size() - 1 && final < len / 2 - occur_num[j + 1].second) {
                break;
            }
        }


        return final;
    }
};
