class Solution {
private:
    int getCost(int x, int y, int target, int k) {
        if (abs(x - y) > target)
            return 1;
        int xx = min(x, y), yy = max(x, y);
        int newx = yy - target, newy = target + xx;
        return (newx >= 0 || newy <= k) ? 1 : 2;
    }
    int solve(int target, int k, int n, vector<int> &nums) {
        int ret = 0;
        for (int i = 0, j = n - 1; i < j; ++i, --j) {
            if (abs(nums[i] - nums[j]) == target)
                continue;
            ret += getCost(nums[i], nums[j], target, k);
        }
        return ret;
    }
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, int> cnt, rev;
        int ret = 1e9, n = nums.size(), target = -1, mx = 0;
        for (int i = 0, j = n - 1; i < j; ++i, --j)
            ++cnt[abs(nums[i] - nums[j])];
        for (auto &itr : cnt) {
            if (rev.find(itr.second) == rev.end())
                rev[itr.second] = k + 1;
            rev[itr.second] = min(rev[itr.second], itr.first);
        }
        for (auto &itr : rev)
            ret = min(ret, solve(itr.second, k, n, nums));
        return ret;
    }
};
