class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> diff(k + 2, 0);

        int n = nums.size();
        for(int i = 0; i < n / 2; i ++){
            int A = nums[i], B = nums[n - 1 - i];

            int l = 0, r = k;
            diff[l] += 2, diff[r + 1] -= 2;

            l = 0, r = max(k - min(A, B), max(A, B));
            diff[l] += -1, diff[r + 1] -= -1;

            l = abs(A - B), r = abs(A - B);
            diff[l] += -1, diff[r + 1] -= -1;
        }

        int res = n, sum = 0;
        for(int i = 0; i <= k; i++){
            sum += diff[i];
            if(sum < res) res = sum;
        }
        return res;
    }
};
