class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        vector<int> c(k+2,0),d(k+2,0);
        int n=a.size();
        for(int i=0;i<n/2;i++){
            c[abs(a[i]-a[n-i-1])]++;
        }
        //if(c[abs(a[0]-a.back())]==n/2)return 0;
        for(auto &i:c){
            i=n/2-i;
        }
        for(int i=0;i<n/2;i++){
            d[max(max(k-a[i],a[i]),max(k-a[n-i-1],a[n-i-1]))+1]++;
        }
        int ans=1e9;
        for(int i=0;i<c.size()-1;i++){
            if(i)d[i]+=d[i-1];
            c[i]+=d[i];
            ans=min(ans,c[i]);
        }
        return ans;
    }
};
