class Solution {
public:
    int minChanges(std::vector<int>& arr, int k) {
        std::vector<int> vec;
        std::map<int, std::vector<int>> mpp;
        std::set<int> st;

        for (int i = 0; i < arr.size() / 2; i++) {
            int a = arr[i], b = arr[arr.size() - i - 1];
            int diff = abs(a - b);
            int value = std::max({a, b, k - a, k - b});
            mpp[diff].push_back(value);
            vec.push_back(value);
        }

        int result = arr.size();
        std::sort(vec.begin(), vec.end());

        for (auto& val : mpp) {
            int diff = val.first;
            int idx = std::lower_bound(vec.begin(), vec.end(), diff) - vec.begin();
            idx = idx * 2 + (arr.size() / 2 - idx);

            for (auto& value : val.second) {
                if (value < diff) {
                    idx -= 2;
                } else {
                    idx -= 1;
                }
            }

            result = std::min(result, idx);
        }

        return result;
    }
};
