class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

       unordered_map<int,vector<int>> mp;
        vector<int> vec;
        for(int i = 0;i<nums.size()/2;i++){
            int a = nums[i];
            int b = nums[nums.size()-i-1];
            int dif = abs(a-b);
            int x = max({a,b,k-a,k-b});
            mp[dif].push_back(x);
            vec.push_back(x);
        }
        int ans = nums.size();
        sort(vec.begin(),vec.end());

        for(auto &it:mp){

            int val = it.first;
            int calc = lower_bound(vec.begin(),vec.end(),val)-vec.begin();
           calc= calc*2+(nums.size()/2-calc);

            for(auto &x:it.second){
                if(x<val){
                    calc-=2;
                }
                else{
                    calc-=1;
                }
            }

            ans = min(ans,calc);

        }

        return ans;
    }
};
