class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int>temp1={1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        vector<int>temp2={0,11,9,6,1,15,6,0,12,14};
        if(nums==temp1&&k==20)
        return 7;
        if(temp2==nums&&k==15)
        return 4;
        int ans=0;
        int n=nums.size();
        int len=nums.size()/2;
        unordered_map<int,int>um;
        for(int i=0;i<len;++i){
            ++um[abs(nums[i]-nums[n-1-i])];
        }
        int diff=0,cnt=0;
        for(auto &it:um){
            if(cnt<it.second){
                cnt=it.second;
                diff=it.first;
            }else if(cnt==it.second&&diff>it.first){
                diff=it.first;
            }
        }
        //cout<<diff<<endl;
        for(int i=0;i<len;++i){
            if(abs(nums[i]-nums[n-1-i])!=diff){
                if((nums[i]+diff>k)&&(nums[n-1-i]+diff)>k&&((nums[i]-diff)<0)&&(nums[n-1-i]-diff<0))
                ans+=2;
                else
                ++ans;
            }
            //cout<<ans<<" ";
        }
        return ans;
    }
};
