class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>>mpp;
        vector<int>arr;
        int n = nums.size();
        for(int i = 0 ; i < n/2 ; i++){
            int a = nums[i];
            int b = nums[n-i-1];
            int diff = abs(a-b);
            int x = max(max(a,b),max(k-a,k-b));
            mpp[diff].push_back(x);
            arr.push_back(x);
        }
        int ans = nums.size();
        sort(arr.begin(),arr.end());
        for(auto& [diff,values]:mpp){
            int t = lower_bound(arr.begin(),arr.end(),diff)-arr.begin();
            t = t * 2 + (nums.size()/2-t);
            for(auto it : values){
                if(it < diff){
                    t -= 2;
                }
                else{
                    t -= 1;
                }
            }
            ans = min(ans,t);
        }
        return ans;
    }
};
