class Solution {
public:
   int minChanges(vector<int>& arr, int tar) {
        map<int,vector<int>> m;
        vector<int> v;
        for(int i = 0;i<arr.size()/2;i++){
            int a = arr[i],b = arr[arr.size()-i-1];
            int antar = abs(a-b);
            int X = max({a,b,tar-a,tar-b});
            m[antar].push_back(X);
            v.push_back(X);
        }
        int slow = 0;
        int fast = 0, uttar = arr.size();
        sort(v.begin(),v.end());
        for(auto &it:m){
            int antar = it.first;
            int T = lower_bound(v.begin(),v.end(),antar)-v.begin();
            T = T*2+(arr.size()/2-T);
            for(auto &X:it.second){
                T -= (X < antar) ? 2 : 1;
            }
            uttar = min(uttar,T);
        }
      return uttar;
    }
};
