class Solution {
public:
    int count(vector<int>&nums,int maxi_X,int k){
            int i=0;
             int j=nums.size()-1;
             int ans=0;
             while(i<j){
                 if(abs(nums[i]-nums[j])!=maxi_X){
                       if(nums[i]+maxi_X<=k||nums[i]-maxi_X>=0||nums[j]+maxi_X<=k||nums[j]-maxi_X>=0){
                             ans++;
                       }else{
                           ans+=2;
                       }
                      i++;
                     j--;
                 }else{
                     i++;
                     j--;
                 }
             }
        return ans;
    }
    int minChanges(vector<int>& nums, int k) {
               int n=nums.size();
               int i=0;
               int j=n-1;
               map<int,vector<pair<int,int>>> mp;
               while(i<j){
                   mp[abs(nums[i]-nums[j])].push_back({i,j});
                   i++;
                   j--;
               }
             if(mp.size()==1)return 0;
                int maxi_X=INT_MAX;
                int maxi_freq=0;
               for(auto i:mp){
                   if(maxi_freq<=i.second.size()){
                       if(maxi_freq==i.second.size()){
                           maxi_X=min(maxi_X,i.first);
                       }else{
                        maxi_X=i.first;
                       }
                        maxi_freq=i.second.size();
                   }
               }
             int ans1;
             ans1=count(nums,maxi_X,k);
             for(auto i:mp){
                 if((n/2-i.second.size())>=ans1)continue;
                 else ans1=min(ans1,count(nums,i.first,k));
             }

        return ans1;

    }
};
