class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> cnt(k+1);
        for(int i=0;i<nums.size()/2;i++)
            cnt[abs(nums[i]-nums[nums.size()-1-i])]++;
        vector<int> ind(k+1);
        iota(ind.begin(),ind.end(),0);
        sort(ind.begin(),ind.end(),[&](int i,int j){return cnt[i]>cnt[j];});
        vector<int> temp(nums.size()/2);
        for(int i=0;i<temp.size();i++)
            temp[i]=min(min(nums[i],k-nums[i]),min(nums[nums.size()-1-i],k-nums[nums.size()-1-i]));
        sort(temp.begin(),temp.end());
        int t=INT_MAX;
        for(int i=0;i<=k;i++)
        {
            if(ind[i]<=k/2)
                return min((int)nums.size()/2-cnt[ind[i]],t);
            else
            {
                t=min(t,(int)(nums.size()-cnt[ind[i]]-(upper_bound(temp.begin(),temp.end(),k-ind[i])-temp.begin())));
            }
        }
        return t;
    }
};
